import React, { useState } from "react";
// Import Component MUI
import {
  Button,
  Container,
  IconButton,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
// Import Icon
import AddPhotoAlternateOutlinedIcon from "@mui/icons-material/AddPhotoAlternateOutlined";
// Import Component
import Navbar from "../Navbar/Navbar";

export default function TambahSertifikat() {
  const [imgPreview, setImgPreview] = useState(null);
  const [error, setError] = useState(false);

  const handleImageChange = (e) => {
    setError(false);
    const selected = e.target.files[0];
    const ALLOWED_TYPES = ["image/png", "image/jpeg", "image/jpg"];
    if (selected && ALLOWED_TYPES.includes(selected.type)) {
      let reader = new FileReader();
      reader.onloadend = () => {
        setImgPreview(reader.result);
      };
      reader.readAsDataURL(selected);
    } else {
      setError(true);
    }
  };

  return (
    <>
      <Container>
        <Navbar>Tambah Sertifikat</Navbar>

        {/* Upload File Gambar */}
        <div className="container">
          <div
            className="imgPreview"
            style={{
              background: imgPreview
                ? `url("${imgPreview}") no-repeat center/cover`
                : "transparent",
              width: "100%",
              height: "250px",
              align: "center",
              marginTop: 80,
            }}
          >
            {!imgPreview && (
              <>
                <IconButton
                  sx={{
                    color: "#249EA0",
                  }}
                  component="label"
                  htmlFor="fileUpload"
                  className="customFileUpload"
                >
                  {/* Icon */}
                  <AddPhotoAlternateOutlinedIcon
                    sx={{
                      height: "80%",
                      width: "80%",
                    }}
                  />
                  <input
                    type="file"
                    hidden
                    id="fileUpload"
                    onChange={handleImageChange}
                  />
                </IconButton>
              </>
            )}
            {error && (
              <Typography className="errorMsg" sx={{ color: "red" }}>
                File not supported!
              </Typography>
            )}
          </div>

          {imgPreview && (
            <Button
              onClick={() => setImgPreview(null)}
              variant="contained"
              color="warning"
              sx={{
                width: "100%",
                height: 45,
                fontSize: 15,
                textTransform: "capitalize",
                mt: -3,
                mb: 1,
              }}
            >
              Remove image
            </Button>
          )}
        </div>

        <Grid container sx={{ mt: 1 }} spacing={2} align={"center"}>
          {/* Nama Events */}
          <Grid item xs={12}>
            <TextField
              placeholder="Nama Events"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
              sx={{
                width: "100%",
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  border: "solid #249EA0",
                  borderRadius: 2,
                },
                "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
              }}
            />
          </Grid>

          {/* Tahun */}
          <Grid item xs={12}>
            <TextField
              placeholder="Tahun"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
              sx={{
                width: "100%",
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  border: "solid #249EA0",
                  borderRadius: 2,
                },
                "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
              }}
            />
          </Grid>

          {/* No Pertandingan */}
          <Grid item xs={12}>
            <TextField
              placeholder="No. Pertandingan"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
              sx={{
                width: "100%",
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  border: "solid #249EA0",
                  borderRadius: 2,
                },
                "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
              }}
            />
          </Grid>

          {/* Juara */}
          <Grid item xs={12}>
            <TextField
              placeholder="Juara"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
              sx={{
                width: "100%",
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  border: "solid #249EA0",
                  borderRadius: 2,
                },
                "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
              }}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              placeholder="Deskripsi"
              multiline
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 85,
                },
              }}
              sx={{
                width: "100%",
                mb: 5,
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  border: "solid #249EA0",
                  borderRadius: 2,
                },
                "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "#249EA0",
                  },
              }}
            />
          </Grid>

          {/* Button Submit */}
          <Grid item xs={12}>
            <Button
              variant="contained"
              color="warning"
              sx={{
                width: "100%",
                height: 45,
                fontSize: 15,
                textTransform: "capitalize",
                mt: -3,
                mb: 1,
              }}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </Container>
    </>
  );
}
