import React from "react";
// Import Component MUI
import {
  Card,
  CardContent,
  CardMedia,
  Divider,
  Grid,
  Typography,
} from "@mui/material";
import { CardActionArea } from "@mui/material";
import { Link } from "react-router-dom";

export default function CardSertificate(props) {
  return (
    <>
      <Grid container direction={{ xs: "column", md: "row", xl: "row" }}>
        {/* Card*/}
        <Grid item xs={12} md={6} xl={6}>
          <Link
            // to={`/detail-sertifikat/${data.id}`}
            to="/detailsertificate"
            style={{ textDecoration: "none" }}
          >
            <Card
              sx={{
                width: {
                  xs: "99%",
                },
                height: {
                  xs: 290,
                },
                mt: 2,
                border: "#249EA0 solid",
              }}
            >
              {/* Image */}
              <CardActionArea>
                <CardMedia
                  component="img"
                  image={props.id}
                  alt="sertifikat"
                  sx={{
                    height: {
                      xs: 200,
                    },
                  }}
                />
              </CardActionArea>

              <Divider />

              {/* Content */}
              <CardContent>
                {/* Title */}
                <Typography
                  sx={{
                    fontSize: {
                      xs: "16px",
                    },
                    fontWeight: "bold",
                    mt: -1.5,
                    mb: 0,
                  }}
                >
                  {props.title}
                </Typography>

                {/* Description */}
                <Typography
                  sx={{
                    mb: 1,
                    fontSize: {
                      xs: "16px",
                    },
                  }}
                >
                  {props.description}
                </Typography>

                {/* Date */}
                <Typography
                  sx={{
                    fontSize: {
                      xs: "14px",
                    },
                    color: "#808080",
                  }}
                >
                  {props.date}
                </Typography>
              </CardContent>
            </Card>
          </Link>
        </Grid>
      </Grid>
    </>
  );
}
