import React, { useState } from "react";
// Import Component MUI
import {
  Button,
  Container,
  IconButton,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import { styled } from "@mui/material/styles";
// Import Icon
import AddPhotoAlternateOutlinedIcon from "@mui/icons-material/AddPhotoAlternateOutlined";
import CancelOutlinedIcon from "@mui/icons-material/CancelOutlined";
// Import Component
import Navbar from "../Component/Navbar";
// Import Theme
import Colors from "../Theme/Color";

export default function AddSertificate() {
  const [imgPreview, setImgPreview] = useState(null);
  const [error, setError] = useState(false);

  // handleImageChange
  const handleImageChange = (e) => {
    setError(false);
    const selected = e.target.files[0];
    const ALLOWED_TYPES = ["image/png", "image/jpeg", "image/jpg"];
    if (selected && ALLOWED_TYPES.includes(selected.type)) {
      let reader = new FileReader();
      reader.onloadend = () => {
        setImgPreview(reader.result);
      };
      reader.readAsDataURL(selected);
    } else {
      setError(true);
    }
  };

  // Costum TextField
  const TextFields = styled(TextField)({
    width: "100%",
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      border: "1.5px solid",
      borderColor: Colors.lightGray,
      borderRadius: 7,
    },
    "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      border: "solid",
      borderColor: Colors.lightGray,
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: Colors.lightGray,
    },
  });

  return (
    <>
      <Container>
        <Navbar>Tambah Sertifikat</Navbar>
        <Grid container sx={{ mt: 7 }} spacing={2} align={"center"}>
          {/* Nama Events */}
          <Grid item xs={12}>
            <TextFields
              placeholder="Nama Kegiatan"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
            />
          </Grid>

          {/* Tahun */}
          <Grid item xs={12}>
            <TextFields
              placeholder="Tahun"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
            />
          </Grid>

          {/* No Pertandingan */}
          <Grid item xs={12}>
            <TextFields
              placeholder="No. Pertandingan"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
            />
          </Grid>

          {/* Juara */}
          <Grid item xs={12}>
            <TextFields
              placeholder="Juara"
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 15,
                },
              }}
            />
          </Grid>

          <Grid item xs={12}>
            <TextFields
              placeholder="Deskripsi"
              multiline
              inputProps={{
                style: {
                  fontSize: 14,
                  height: 85,
                },
              }}
            />
          </Grid>
        </Grid>

        {/* Upload File Image */}
        <div className="container">
          {/* Delete Image */}
          <div align={"left"}>
            {imgPreview && (
              <IconButton
                onClick={() => setImgPreview(null)}
                variant="contained"
                color="error"
                sx={{
                  height: 40,
                  mb: -6,
                  mr: -1,
                }}
              >
                <CancelOutlinedIcon sx={{ fontSize: 16 }} />
              </IconButton>
            )}
          </div>

          {/* Image Preview */}
          <div
            className="imgPreview"
            style={{
              background: imgPreview
                ? `url("${imgPreview}") no-repeat center/cover`
                : "transparent",
              width: "50%",
              height: "120px",
            }}
            align={"center"}
          >
            {!imgPreview && (
              <>
                <IconButton
                  sx={{
                    color: Colors.seaGreen,
                  }}
                  component="label"
                  htmlFor="fileUpload"
                  className="customFileUpload"
                >
                  {/* Icon */}
                  <AddPhotoAlternateOutlinedIcon
                    sx={{
                      height: "20%",
                      width: "20%",
                    }}
                  />
                  <input
                    type="file"
                    hidden
                    id="fileUpload"
                    onChange={handleImageChange}
                  />
                </IconButton>
                <Typography fontSize={10}>Upload Gambar</Typography>
              </>
            )}

            {/* Error */}
            {error && (
              <Typography className="errorMsg" sx={{ color: "red", mt: -2 }}>
                File not supported!
              </Typography>
            )}
          </div>
        </div>

        <Grid Container>
          {/* Button Submit */}
          <Grid item xs={12}>
            <Button
              variant="contained"
              color="warning"
              sx={{
                width: "100%",
                height: 45,
                fontSize: 15,
                textTransform: "capitalize",
                mt: 1,
                mb: 1,
              }}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </Container>
    </>
  );
}
